var $j = jQuery.noConflict();

// Global configuration
var thbConfig = {
	'contentOutSpeed': 250,
	'contentOutEasing': 'easeOutQuad',
	'contentInSpeed': 180,
	'contentInEasing': 'easeOutQuad',
	'preloadDelay': 350,
	'preloadTransition': 350,
	'preloadEasing': 'linear'
};

$j(document).ready(function() {

	var div = document.createElement('div');
	div.setAttribute('style', 'transition:top 1s ease;-webkit-transition:top 1s ease;-moz-transition:top 1s ease;');
	cssTransitionsSupported = !!(div.style.transition || div.style.webkitTransition || div.style.MozTransition);
	delete div;

	if( cssTransitionsSupported )
		jQuery("body").addClass("transitions");
	else
		jQuery("body").addClass("no-transitions");

	gmap();

	// Menu
	$j("#main-nav").menu({
		speed: 150,
		hideCallback: function() {
			$j(window).trigger("resize");
		}
	});

	$j("#main-nav > ul.menu, #main-nav > div.menu > ul").mobileMenu();

	// Toggles
	$j(".slide_trigger").toggle({
		speed: 150
	});

	// Accordions
	$j(".accordion").accordion({
		speed: 150
	});

	// Tabs
	$j(".tabs").tabs({
		speed: 150
	});

	// Portfolio filter
	filter_items( $j("#filterlist"), $j("#worklist"), '.item' );


	// Overlay 
	var overlay_opacity = ($j.browser.msie && parseInt($j.browser.version) < 9) ? 0.7 : 1;
	$j(".item-image, .list-thumb")
		.mouseenter(function() {
			var overlay = $j(this).find(".overlay");
			overlay
				.stop()
				.animate({
					'opacity': overlay_opacity
				}, 180, "easeOutQuad");
		})
		.mouseleave(function() {
			var overlay = $j(this).find(".overlay");
			overlay
				.stop()
				.animate({
					'opacity': 0
				}, 180, "easeOutQuad");
		});	

	// Preload
	$j(".item-image, .list-thumb").preload({
		'image_delay': thbConfig.preloadDelay,
		'image_transition': thbConfig.preloadTransition,
		'image_easing': thbConfig.preloadEasing
	});

	// Page galleries
	$j(".text a:not(.no-thumb, .item-image):has(img)").attr("rel", "prettyPhoto[pp_gal]");
	
	// Post format gallery slideshow
	$j(".post-gallery").flexslider();
	$j(".post-gallery").each(function(i) {
		$j(this).find("a:has(img)").attr("rel", "prettyPhoto[pp_gal_" +i+ "]");
	});

	// Gallery shortcode rel
	$j(".post-gallery a:has(img)").each(function(index, el) {
		var gallery_id = $j(this).parents(".gallery").attr("id");
		$j(this).attr("rel", "prettyPhoto[pp_gal_"+ gallery_id +"]");
	});

	// Excluding single works' thumbs
	$j(".single-works a.item-image.thumb").attr("rel", "");
	
	// Prettyphoto
	var prettyPhotoOptions = {
		allow_resize: true,
		hideflash: true,
		show_title: false,
		theme: 'pp_default',
		social_tools: ''
	};

	var prettyPhotoObjects = $j(".gallery a, a[rel^='prettyPhoto']");
	prettyPhotoObjects.prettyPhoto(prettyPhotoOptions);

	$j('p')
	    .filter(function() {
	        return $j.trim($j(this).html()) === ''
	    })
	    .remove();
});


// Hovering menu items ============================================================================

function hover_menu_items() {
	var menu_primary = $j(".menu > li > a");
	var menu_hover_class = "menu-item-hover";

	menu_primary.each(function(i,e) {
		var parent = $j(e).parents("li");

		$j(e).mouseover(
			function() {
				parent.addClass(menu_hover_class);
			}			
		);
		$j(e).mouseout(
			function() {
				parent.removeClass(menu_hover_class);
			}
		);

		var submenu = parent.find("ul");

		submenu.mouseover(
			function() {
				parent.addClass(menu_hover_class);
			}			
		);
		submenu.mouseout(
			function() {
				parent.removeClass(menu_hover_class);
			}
		);
	});
}

// Validate =======================================================================================

function thb_validate(){
	// form validation						 
	$j('.contact-form').validate({
		rules: {	
			contact_name: {
				required: true,
				minlength: 2
			},
			contact_email: {
				required: true,
				email: true
			},
			contact_message: {
				required: true,
				minlength: 10
			}
		},
		submitHandler: function(form) {
			$j(form).ajaxSubmit({
				target: "#result",
				resetForm: false
			});
		}
	});
}

// Filter =========================================================================================

function filter_items(filterlist, itemscontainer, itemsclass) {

	// Filter selection
	var filtertriggers = filterlist.find("li a");

	// Temporarily hiding the filter
	filterlist
		.css({ visibility: 'hidden', opacity: 0 });
	
	filtertriggers.each(function() {
		var trigger = $j(this),
			href = trigger.attr("href").replace("#", "");

		if( href == 'all' || $j(itemsclass + "." + href).length > 0 ) {

			trigger.click(function() {
				filterlist.find("li").removeClass("current");
				trigger.parent().addClass("current");

				// Options
				var options = {};
				if( href != 'all' )
					options.filter = "."+href;
				else
					options.filter = itemsclass;

				// Fire isotope
				itemscontainer.isotope(options);

				return false;
			});

		}
		else if( href != 'all' )
			trigger.parent().remove();
	});

	// Showing the filter again
	filterlist
		.css({ visibility: 'visible' })
		.delay(1000)
		.animate({ opacity: 1 }, 250);

	itemscontainer.imagesLoaded(function() {
		
		setTimeout(function() {
			itemscontainer.isotope({
				itemSelector: itemsclass,
				layoutMode: 'fitRows',

				animationOptions: {
					duration: 250,
					easing: 'linear',
					queue: false
				}
			});
		}, 10);
		
		itemscontainer.find(".item-image").preload();
		// filterlist.find("li.current a").trigger("click");

	});

}

function gmap(){

	var mapElements = $j(".map");
	mapElements.each(function(i, mapElement) {
		var myLatlng = [];
		var address = [];

		// Data
		var data_latlong = $j(mapElement).attr("data-latlong");

		var data_latlong_split = data_latlong.split(",");

		var data_lat = data_latlong_split[0];
		var data_long = data_latlong_split[1];
		var data_zoom = $j(mapElement).attr("data-zoom");
		var data_address = $j(mapElement).attr("data-address");
		var data_height = $j(mapElement).attr("data-height");
		var data_width = $j(mapElement).attr("data-width");

		myLatlng.push(new google.maps.LatLng(data_lat, data_long));

		var myOptions = {
			zoom: parseInt(data_zoom),
			mapTypeControl: false,
			streetViewControl: false,
			scrollwheel: false,
			zoomControlOptions: {
				style: google.maps.ZoomControlStyle.SMALL
			},
			center: myLatlng[0],
			mapTypeId: google.maps.MapTypeId.ROADMAP
		}
		var map = new google.maps.Map(mapElement, myOptions);

		var num_markers = myLatlng.length;

		var infowindow = new google.maps.InfoWindow({
			content: ""
		});
		
		if( data_address != '' )
			address.push(data_address);

		var marker_title = (address.length > 0) ? address[0] : "";
		
		var marker = new google.maps.Marker({
	    	position: myLatlng[0],
	    	map: map,
	    	title: marker_title,	        	
	    	animation: google.maps.Animation.DROP     	       	  
		});
		google.maps.event.addListener(marker, 'click', function() {
			infowindow.close();
			infowindow.setContent("<div style='overflow:hidden;margin-right:20px;font-size:1.3em;line-height:1.5em;font-weight:bold;'><h1>"+this.title+"</h1></div>");
			infowindow.open(map, this);
	    });

		$j(mapElement).css({
	    	height: data_height,
	    	width: data_width
	    });
	});

}


